var storage = new MemStorage();

// shared/schema.ts
import { pgTable, text, serial, boolean, real } from "drizzle-orm/pg-core";
import { createInsertSchema } from "drizzle-zod";
var users = pgTable("users", {
  id: serial("id").primaryKey(),
  username: text("username").notNull().unique(),
  password: text("password").notNull()
});
var sensorData = pgTable("sensor_data", {
  id: serial("id").primaryKey(),
  timestamp: text("timestamp").notNull(),
  temperature: real("temperature"),
  humidity: real("humidity"),
  soilMoisture: real("soil_moisture"),
  airQuality: real("air_quality"),
  pumpStatus: boolean("pump_status"),
  rainStatus: boolean("rain_status"),
  locationLat: real("location_lat"),
  locationLng: real("location_lng")
});
var alertLogs = pgTable("alert_logs", {
  id: serial("id").primaryKey(),
  timestamp: text("timestamp").notNull(),
  type: text("type").notNull(),
  // 'warning', 'error', 'info', 'success'
  title: text("title").notNull(),
  message: text("message").notNull()
});
var insertUserSchema = createInsertSchema(users).pick({
  username: true,
  password: true
});
var insertSensorDataSchema = createInsertSchema(sensorData).omit({
  id: true
});
var insertAlertLogSchema = createInsertSchema(alertLogs).omit({
  id: true
});

// server/routes.ts
import { z } from "zod";
async function registerRoutes(app2) {
  app2.post("/api/sensor-data", async (req, res) => {
    try {
      const data = insertSensorDataSchema.parse(req.body);
      await storage.saveSensorData(data);
      return res.status(201).json({ message: "Sensor data saved successfully" });
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid sensor data", errors: error.errors });
      }
      console.error("Error saving sensor data:", error);
      return res.status(500).json({ message: "Failed to save sensor data" });
    }
  });
  app2.get("/api/sensor-data/latest", async (req, res) => {
    try {
      const latestData = await storage.getLatestSensorData();
      return res.status(200).json(latestData);
    } catch (error) {
      console.error("Error retrieving latest sensor data:", error);
      return res.status(500).json({ message: "Failed to retrieve latest sensor data" });
    }
  });
  app2.post("/api/pump-status", async (req, res) => {
    try {
      const { status } = req.body;
      if (typeof status !== "boolean") {
        return res.status(400).json({ message: "Invalid pump status" });
      }
      return res.status(200).json({ message: "Pump status updated", status });
    } catch (error) {
      console.error("Error updating pump status:", error);
      return res.status(500).json({ message: "Failed to update pump status" });
    }
  });
  const httpServer = createServer(app2);
  return httpServer;
}

